# scripts/0210_NorthernPassage.s

.align 4

NorthernPassage_MapInitCode:
    bx lr

NorthernPassage_MapInitScript:
    end

NorthernPassage_Script1:
    lock
    faceplayer
    msgbox NorthernPassage_Script1_Text
    release
    end

NorthernPassage_Script1_Text:
    .string "Hi there!\n"
    .string "I'm practicing spinning.\p"
    .string "My hobby is to make speedrunners cry\n"
    .string "when I notice them with my sick moves.\p"
    .string "If werster ever comes back to Emerald\n"
    .string "Glitchless, I'll be there for him!$"

NorthernPassage_Script2:
    lock
    faceplayer
    msgbox NorthernPassage_Script2_Text
    release
    end

NorthernPassage_Script2_Text:
    .string "Normally, I would tell you to stay\n"
    .string "away from the tall grass.\p"
    .string "But here's the thing - we don't get\n"
    .string "any wild encounters in this area.\l"
    .string "It's really kind of disappointing.$"

NorthernPassage_Script3:
    lock
    faceplayer
    msgbox NorthernPassage_Script3_Text
    release
    end

NorthernPassage_Script3_Text:
    .string "Have you heard the recent news?\n"
    .string "Brand new, unpopulated areas were\l"
    .string "discovered in Northern Glitchland.\p"
    .string "The discovery was made by scientists\n"
    .string "from the Glitch Research Lab.\p"
    .string "They say they used something called\n"
    .string "a Walk Through Walls glitch.\l"
    .string "Very interesting!$"

