TheTrendsetters_SendPhrase:
    push {lr}
    # copy new phrase to local buffer
    ldr r1, =gStringVar2
    ldr r2, =TrendyPhraseString    
    mov r3, #26
    bl TheTrendsetters_SendPhrase_CopyData
