	playse SE_SUCCESS
    msgbox YeetGrounds_OK
    closemessage
    delay 15
    playse SE_DOOR
    setmetatile \tx, \ty, 0x26a, FALSE
	callnative DrawWholeMapView
    delay 20
    release
    end
.endm

